import math

def hex_to_dec(input):
    hex_string = input.lower()
    x = 1

    while x < (len(hex_string) + 1):
        if hex_string[-x] not in str(list(range(0,10))):
            yield (int(ord(hex_string[-x]) - 87) * (16**(x-1)))
            x += 1

        else:
            yield ((int(hex_string[-x])) * (16**(x-1)))
            x += 1

print (sum(list(hex_to_dec('5CfA1FFc4829BF21f2C6e122764640380ae96Cb6'))))

def dec_to_hex(input):
    dec_number = int(input)

    while dec_number > 0:
        if (dec_number % 16) > 9:
            yield (chr(int(dec_number % 16) + 87))

        else:
            yield (str(int(dec_number % 16)))

        dec_number = math.floor(dec_number / 16)

print (''.join(list(dec_to_hex(''))))
